<script>


export default {
  name: "Footer",
  data() {
    return {
      day:0,
      hour:0,
      minute:0,
      second:0
    };
  },
  methods: {

  },
  mounted() {
       
        var agoTimer = new Date("1997/08/30").getTime()/1000;
    

        setInterval(()=>{
          var newTimer = new Date().getTime()/1000;
          //获取之间的时间戳差值
          let useDay = newTimer - agoTimer;
          //对差值进行换算，得到相应的单位
          this.day = Math.floor(useDay/86400);
          this.hour  = Math.floor(useDay%86400/3600);
          this.minute  = Math.floor(useDay%86400%3600/60);
          this.second  = Math.floor(useDay%86400%3600%60);
        },1000)
  }
};
</script>


